changeSet.sql:
			update clinlims.menu
			set action_url='/Report.do?type=patient&report=Trends'
			where action_url='/Report.do?type=patient&report=ForCIDashboard';

			update clinlims.menu
			set presentation_order = 30 
			where action_url='/Report.do?type=patient&report=Trends';

			update clinlims.menu
			set action_url='/Report.do?type=patient&report=CIStudyExport'
			where action_url='/Report.do?type=patient&report=CISampleExport';

			update clinlims.menu
			set tool_tip_key='tooltip.export.specific'
			where action_url='tooltip.export.cpecific';


			INSERT INTO clinlims.menu(id, parent_id, presentation_order, element_id, action_url, click_action, 
            display_key, tool_tip_key, new_window, is_active)
			VALUES (nextval('clinlims.menu_seq'),(select id from clinlims.menu where element_id='menu_reports_export'),20,'menu_reports_export_valid','/Report.do?type=patient&report=CISampleExport',default,'reports.export.valid','tooltip.export.valid',default,default);
